INSERT INTO `hotel` VALUES (1, 3261, 8.5, 4, 1896, 1, '四季', '酒店位置优越，可俯瞰地标建筑小蛮腰，视野很赞。酒店旁边就是地铁站，出行非常方便，晚上附近的花城广场不时会有音乐水舞表演，很有气氛。', '珠江新城', 321, 19, 'http://p1-q.mafengwo.net/s10/M00/E7/E3/wKgBZ1mMtJSAVSqAAB08VlcwpnA68.jpeg?imageMogr2%2Fthumbnail%2F%21660x480r%2Fgravity%2FCenter%2Fcrop%2F%21660x480%2Fquality%2F90', 10, 85, 'Four Seasons Hotel Guangzhou', 1, '广州四季酒店', 'https://www.mafengwo.cn/hotel/5423325.html?iMddid=10088');
INSERT INTO `hotel` VALUES (2, 3261, 8.6, 5, 963, 2, '白天鹅', '宾馆庭园式设计非常复古，装饰很典雅。客房是现代岭南风格的装饰，在窗边可欣赏到白鹅潭的景致，房间温馨舒适。宾馆周边公共设施也齐全。', '沙面,上下九', 432, 199, 'http://n1-q.mafengwo.net/s11/M00/47/EF/wKgBEFtiXJaAZlXJAAN7kTd9fvc15.jpeg?imageMogr2%2Fthumbnail%2F%21660x480r%2Fgravity%2FCenter%2Fcrop%2F%21660x480%2Fquality%2F90', 20, 90, 'White Swan Hotel', 0, '广州白天鹅宾馆', 'https://www.mafengwo.cn/hotel/25061.html?iMddid=10088');
INSERT INTO `hotel` VALUES (3, 3261, 8.3, 3, 372, 3, '白天鹅', '酒店地理位置很好，紧邻珠江，早上可以晨练。酒店环境清幽，装修典雅，拥有各类房型，娱乐休闲设施也齐全，房间干净舒适。', '沙面,上下九', 268, 17, 'http://p1-q.mafengwo.net/s11/M00/56/FC/wKgBEFtxRyaAETCIAAJh7IIKQJ438.jpeg?imageMogr2%2Fthumbnail%2F%21660x480r%2Fgravity%2FCenter%2Fcrop%2F%21660x480%2Fquality%2F90', 0, 50, 'Guangdong Victory Hotel', 1, '广东胜利宾馆', 'https://www.mafengwo.cn/hotel/64157.html?iMddid=10088');
