INSERT INTO `travel` VALUES (1, 3261, 1, '广州三天三夜|跟随必字榜吃喝玩乐耍', '已经数不清是第几次 广州 行了，归根结底，仍然是因为吃呀。跟 香港 一样， 广州 总觉得遍地都好吃的，而 广州 人的爱吃也是全国出了名的， 比如 有一句话流传“ 广东 人连 福建 人都吃”！身为 福建 人，表示 亚历山大 呀。那么这一次，以吃为名，去 广州 ，跟着大众点评“必字榜”，吃住玩一条龙，且看我攻略。', '/wKgE2l0ZXLaAQ9X7AAg3M8RobGA39.jpeg', '2019-06-23 00:00:00', 3000, 3, 1, '2019-07-01 08:03:00', '2019-07-01 08:03:00', '2019-07-01 08:03:00', 1, 26, 23, 36, 63, 13, 2);
INSERT INTO `travel` VALUES (2, 3261, 1, '每年都会有的广州情节', '几乎每年都会去 广州 一趟，一般都是一两天，一个人逛。', '/wKgEaVzD69aAV3wVAApzcYZfk6089.jpeg', '2019-04-27 23:39:00', 100, 1, 1, '2019-04-27 23:39:00', '2019-04-27 23:39:00', '2019-04-27 23:39:00', 1, 52, 85, 70, 72, 96, 2);
INSERT INTO `travel` VALUES (3, 3261, 1, '记匆忙的广州四日游', '这是一趟说走就走的旅行。2019年1月28日，在寒冷的且没有暖气的江浙沪，我们家一家三口突然决定逃离寒冷，去往真正的南方— 广州 ！', '/wKgEaVxps2OAG1-cAAg3UXjgz7M20.jpeg', '2019-01-30 00:00:00', 100, 4, 1, '2019-02-24 22:27:00', '2019-02-24 22:27:00', '2019-02-24 22:27:00', 1, 71, 42, 94, 44, 45, 2);
INSERT INTO `travel` VALUES (4, 249, 1, '摩登而复古，旖旎又深沉，梦幻到极致', '小时候来 上海 并没有什么感觉，只感觉 上海 挺发达科技感挺强的；', '/wKgEaVxnsbaAR3cHAAKyMnIeZB425.jpeg', '2019-02-08 00:00:00', 3500, 5, 2, '2019-02-16 15:29:00', '2019-02-16 15:29:00', '2019-02-16 15:29:00', 1, 86, 99, 37, 27, 87, 2);
INSERT INTO `travel` VALUES (5, 249, 1, '5月趣上海，非去不可', '有时候，人必须远行，才能发现近在咫尺的东西，2岁的这几个月，小脚成长很快，大小便控制自如，让我这个老妈子颇具成就感;语言进步明显，词汇量飞速增加;开始能安静下来而且能坐得住。', '/wKgE2l0ceZWATUSUAA0zDKigyFs01.jpeg', '2019-05-06 00:00:00', 3500, 5, 2, '2019-07-03 18:47:00', '2019-07-03 18:47:00', '2019-07-03 18:47:00', 1, 73, 86, 11, 61, 99, 2);
INSERT INTO `travel` VALUES (6, 249, 1, '上海海昌海洋公园', '上海 一行，最后一天我们逛了逛新开的海洋公园。 上海 这个季节正是梅雨季，所以天气凉爽。', '/wKgE2l0aD42AUMzWAAshp0JkpnA725.jpg', '2019-06-30 00:00:00', 200, 1, 3, '2019-07-02 07:24:00', '2019-07-02 07:24:00', '2019-07-02 07:24:00', 1, 11, 69, 14, 79, 64, 2);
INSERT INTO `travel` VALUES (7, 241, 1, '同一个故宫，不同的气质，你的朋友圈po照如何更好看', '来 北京 的次数并不多，这次竟然空出了半天，之前在 日本 还留下半卷胶片没拍完，酒店附近实在没什么可拍的，又不想浪费晴空万里的好天气，于是临时决定早起去故宫走一趟，也因为这两年的故宫实在太能“卖萌”了。', '/wKgEaVzOmtWANE-mAALawkDI2mc48.jpeg', '2019-03-20 00:00:00', 200, 1, 1, '2019-05-05 16:09:00', '2019-05-05 16:09:00', '2019-05-05 16:09:00', 1, 4, 82, 0, 63, 52, 2);
INSERT INTO `travel` VALUES (8, 241, 1, '简单的北京之旅', '北京 算是第一次出去旅行，时间比较短，也没做任务的攻略，所以拍的比较简单，都不太充分，很多地方都没去上，真的挺可惜的。', '/wKgIDVxdqB2AaS94AAuNYA8xz8s31.jpeg', '2018-11-12 00:00:00', 200, 5, 5, '2019-02-12 10:13:00', '2019-02-12 10:13:00', '2019-02-12 10:13:00', 1, 60, 9, 67, 32, 6, 2);
INSERT INTO `travel` VALUES (9, 241, 1, '世园会—你值得打卡的地方', '常看新闻的老人们一定会感叹，不管是不是住在 北京 ，都有必要去欣赏一下世园会~据说这是未来十年我国举办的级别最高、规模最大的专业类世园会哦~', '/wKgE2l0Bp9-ARit2AAeLaBkjPCE43.jpeg', '2019-04-29 00:00:00', 100, 1, 3, '2019-06-13 09:56:00', '2019-06-13 09:56:00', '2019-06-13 09:56:00', 1, 43, 20, 72, 79, 99, 2);
INSERT INTO `travel` VALUES (10, 3356, 1, '再见成都', '从大学时候就被来自 四川 的同学安利春熙路的美女、 成都 串串、郫县豆瓣等等，个人最感兴趣的还是美食了，曾经立下目标，要“吃”下几座城市： 广州 、 北京 、 西安 、 成都 、 武汉 、 南京 、 长沙 。现在来看，还剩下 南京 和 长沙 两个目标了。当然走过的地方多了会发现，每个地方都有很好吃的美食美景， 比如 哈尔滨 、 上海 、 杭州', '/wKgE2l0Ux9aAU1NVAAvghJ2xVDs93.jpeg', '2019-06-01 00:00:00', 2000, 2, 4, '2019-07-02 21:38:00', '2019-07-02 21:38:00', '2019-07-02 21:38:00', 1, 98, 55, 83, 95, 49, 2);
INSERT INTO `travel` VALUES (11, 193, 1, '超详细易懂的泰国攻略，曼谷+普吉7天自由行', '哈喽大家好，我是花生，是一个热爱旅游，梦想吃遍全世界的摄影师，说到 泰国 ，相信很多小伙伴们都不陌生，但是也还有很多朋友没去过 泰国 ，或者没有来一次自由行，这里花生给打算第一次去 泰国 的小伙伴们弄出这篇攻略性很强的游记，有美食，有美景，还有同行可爱的伙伴们，下面先让这个视频和图片目录领略下 曼谷 和普吉的魅力吧，下面有实用干货，让你一篇搞定 泰国 自由行准备。', '/wKgE2l0MmZ-ALpA5AAsArQDM8NM53.jpeg', '2019-06-09 00:00:00', 6000, 8, 2, '2019-07-02 19:07:00', '2019-07-02 19:07:00', '2019-07-02 19:07:00', 1, 30, 64, 33, 59, 71, 2);
